const Discord = require('discord.js')
const client = new Discord.Client()

//Mobile Status
const Contstants = require("../../discord.js/src/util/Constants")
Contstants.DefaultOptions.ws.properties.$browser = 'Discord iOS'

function mobile(msg) {
    client.on('ready', () => {
        client.user.setActivity(msg.STATUS, {
            type: 2,
            $browser: 'Discord iOS'
        });
    })
}

module.exports.mobile = mobile;